// Generate Skills from Data
const generateAllSkills = () => {
  const skillsGrids = document.querySelectorAll(".skills-grid");

  // Clear existing skills
  skillsGrids.forEach((grid) => {
    grid.innerHTML = "";
  });

  // Generate skills from data
  SKILLS_DATA.forEach((skill, index) => {
    if (skillsGrids[skill.category]) {
      const skillItem = document.createElement("div");
      skillItem.className = "skill-item enhanced-skill";
      skillItem.dataset.skillIndex = index;

      skillItem.innerHTML = `
        <div class="skill-icon" style="--skill-color: ${skill.color}; background: ${skill.color}">
          ${skill.icon}
        </div>
        <div class="skill-content">
          <h4>${skill.name}</h4>
          <div class="skill-meta">
            <span class="skill-years">${skill.years} years</span>
            <span class="skill-description">${skill.description}</span>
          </div>
        </div>
        <div class="skill-glow" style="--glow-color: ${skill.color}"></div>
      `;

      skillsGrids[skill.category].appendChild(skillItem);
    }
  });

  // Apply enhanced hover effects after generating
  setTimeout(() => {
    addEnhancedHoverEffects();
  }, 100);

  console.log(
    `Generated ${SKILLS_DATA.length} skills across ${skillsGrids.length} categories`
  );
};

// Enhanced Hover Effects System - Disabled
const addEnhancedHoverEffects = () => {
  console.log("Hover effects disabled");
};

// Skills Animation System
const initLuxurySkillsAnimation = () => {
  const skillItems = document.querySelectorAll(".skill-item");
  if (skillItems.length === 0) return;

  // Reset initial state
  skillItems.forEach((item) => {
    item.classList.remove("luxury-reveal");
    item.style.opacity = "0";
    item.style.transform = "translateY(100px) scale(0.5) rotateY(-180deg)";
    item.style.filter = "blur(10px)";
  });

  // Animate by category
  const skillCategories = document.querySelectorAll(".skills-category");
  skillCategories.forEach((category, categoryIndex) => {
    const categorySkillItems = category.querySelectorAll(".skill-item");

    if (categorySkillItems.length > 0) {
      const observer = new IntersectionObserver(
        (entries) => {
          entries.forEach((entry) => {
            if (entry.isIntersecting) {
              categorySkillItems.forEach((item, itemIndex) => {
                setTimeout(() => {
                  item.classList.add("luxury-reveal");
                  setTimeout(() => {
                    item.style.setProperty(
                      "--shimmer-delay",
                      `${itemIndex * 0.1}s`
                    );
                  }, 800);
                  createLuxuryParticles(item);
                }, itemIndex * 200 + categoryIndex * 100);
              });
              observer.unobserve(entry.target);
            }
          });
        },
        {
          threshold: 0.3,
          rootMargin: "0px 0px -100px 0px",
        }
      );
      observer.observe(category);
    }
  });
};

// Create particles effect
const createLuxuryParticles = (skillItem) => {
  const rect = skillItem.getBoundingClientRect();
  const particleCount = 6;
  const colors = [
    "rgba(255, 145, 73, 0.8)",
    "rgba(255, 255, 255, 0.9)",
    "rgba(187, 251, 255, 0.7)",
    "rgba(78, 113, 255, 0.6)",
  ];

  for (let i = 0; i < particleCount; i++) {
    const particle = document.createElement("div");
    const color = colors[Math.floor(Math.random() * colors.length)];
    const size = Math.random() * 6 + 3;
    const delay = Math.random() * 0.5;
    const duration = 2 + Math.random();

    particle.style.cssText = `
      position: fixed;
      width: ${size}px;
      height: ${size}px;
      background: ${color};
      border-radius: 50%;
      pointer-events: none;
      z-index: 1000;
      left: ${rect.left + rect.width / 2}px;
      top: ${rect.top + rect.height / 2}px;
      opacity: 0;
      animation: luxuryParticleFloat ${duration}s ease-out ${delay}s forwards;
    `;

    document.body.appendChild(particle);
    setTimeout(() => particle.remove(), 3000);
  }

  // Add particle animation styles
  if (!document.querySelector("#particle-animation-style")) {
    const style = document.createElement("style");
    style.id = "particle-animation-style";
    style.textContent = `
      @keyframes luxuryParticleFloat {
        0% {
          opacity: 0;
          transform: translate(0, 0) scale(0) rotate(0deg);
        }
        20% {
          opacity: 1;
          transform: translate(
            calc(cos(${Math.random() * Math.PI * 2}rad) * 50px),
            calc(sin(${Math.random() * Math.PI * 2}rad) * 50px)
          ) scale(1) rotate(90deg);
        }
        80% {
          opacity: 0.6;
          transform: translate(
            calc(cos(${Math.random() * Math.PI * 2}rad) * 80px),
            calc(sin(${Math.random() * Math.PI * 2}rad) * 80px)
          ) scale(0.8) rotate(270deg);
        }
        100% {
          opacity: 0;
          transform: translate(
            calc(cos(${Math.random() * Math.PI * 2}rad) * 100px),
            calc(sin(${Math.random() * Math.PI * 2}rad) * 100px)
          ) scale(0) rotate(360deg);
        }
      }
    `;
    document.head.appendChild(style);
  }
};

// Public API for Skills Manager
window.SkillsManager = {
  generateAllSkills,
  initLuxurySkillsAnimation,
  createLuxuryParticles,
  addEnhancedHoverEffects,
  SKILLS_DATA,
};

console.log("✨ Skills Manager loaded successfully");
